PREFIX skos: <http://www.w3.org/2004/02/skos/core#>
SELECT ?entity ?entityLabel WHERE {
    ?entity skos:prefLabel ?entityLabel
    FILTER ( lang(?entityLabel) = "en")
}

PREFIX skos: <http://www.w3.org/2004/02/skos/core#>
SELECT ?entity ?entityLabel WHERE {
    GRAPH <https://publications.europa.eu/resource/authority/frequency> {
      ?entity skos:prefLabel ?entityLabel
      FILTER ( lang(?entityLabel) = "en")
    }
}

